    private string _newLightName = "New light";
    private string baseUrl = "https://localhost:5002/";
    private Light[] _lights = new Light[0];

    protected override async Task OnInitializedAsync() => await GetLights();

    private async Task AddLight()
    {
        var _httpClient = new HttpClient { BaseAddress = new Uri(baseUrl) };
        var addItem = new Light { Name = _newLightName, IsOn = false };
        await _httpClient.PostJsonAsync("api/Lights", addItem);
        await GetLights();
    }

    private async Task GetLights(){
        var _httpClient = new HttpClient { BaseAddress = new Uri(baseUrl) };
        _lights = await _httpClient.GetJsonAsync<Light[]>("api/Lights");
    }

    private async void SwitchLight(Light light){
        light.IsOn = !light.IsOn;
        await UpdateLight(light);
    }

    private async Task UpdateLight(Light light){
        var _httpClient = new HttpClient { BaseAddress = new Uri(baseUrl) };
        await _httpClient.PutJsonAsync($"api/Lights/{light.Id}", light);
    }


